		ContentObject objSkip0 = null;
		ContentObject objSkip2 = null;

		ContentObject object = null;
		responseObjects = new ArrayList<ContentObject>();

		checkResponder();


		CCNTime t1;
		CCNTime t2;
		CCNTime t3;
		CCNTime t4;
		CCNTime skipTime;
		CCNTime skipTime2;

		long timeout = 5000;

		t1 = new CCNTime();
