private int getLineNumber() {
    try {
      String text = getText();
      int columnIndex = text.indexOf(':');
      text = columnIndex == -1 ? text : text.substring(0, columnIndex);
      return Integer.parseInt(text);
    } catch (NumberFormatException e) {
      return -1;
    }
  }

