public static int getTimeZoneOffset(TimeZone timeZone) {
		int offset = timeZone.getRawOffset();

		boolean inDaylightTime = timeZone.inDaylightTime(new Date());

		if (inDaylightTime) {
			offset += timeZone.getDSTSavings();
		}

		return offset;
	}

