        Pair<Annotation, InputStream> pair = serializer.read(kis);
        pair.second.close();
        Annotation readDoc = pair.first;
        kis.close();

        sameAsRead(doc, readDoc);
      } catch (Exception e) { throw new RuntimeException(e); }
    }
  }

